import React, { useEffect, useState } from "react";
import UpdateUnit from "./UpdateUnit";
const Unavailable = () => {
  const [units, setUnits] = useState([]);
  const [show, setShow] = useState(false)
  useEffect(() => {
    fetch("/storage_units")
      .then((r) => r.json())
      .then((data) => setUnits(data));
  }, []);
  function handleShow() {
    setShow(!show)
    console.log("show changed")
}
  function handleDelete(item) {
    console.log(item.id)
    fetch(`storage_units/${item.id}`, {
      method: "DELETE",
      headers: {
        "Content-Type": "application/json",
      }
    });
  }

  return (
    <div>
      {units
        .filter((unit) => unit.status === false)
        .map((unit) => (
          <div
            className="card mb-3"
            style={{ maxWidth: "540px" }}
            key={unit.id}
          >
            <div className="row g-0">
              <div className="col-md-4">
                <img
                  src={unit.image_url}
                  className="img-fluid rounded-start"
                  alt="..."
                />
              </div>
              <div className="col-md-8">
                <div className="card-body">
                  <h5 className="card-title">Unit Number: {unit.name}</h5>
                  <p className="card-text">
                    This unit is either occupied or under maintainance
                  </p>
                </div>
              </div>
            </div>
            <></>{show ? <UpdateUnit/> : null}
            <div className="btns">
              <button className="edit" onClick={()=>handleShow}>Edit</button>
              <button className="delete" onClick={()=>handleDelete(unit)}>Delete</button>
            </div>
          </div>
        ))}
    </div>
  );
};

export default Unavailable;
