import React, { useEffect, useState } from 'react'
import storage from ".//home/moringa/storage-center/src/components/images/Storage-PNG-Images 1.png";
const Home = () => {
    const [content, setContent] = useState([])

    useEffect(() => {
        fetch('/storage_units')
            .then(r => r.json())
        .then(data => setContent(data))
    },[])
    return (
        <>
            <div className="section">

            </div>
        <div className="main">
          {content.map((cont) => (
            <div key={cont.id} className="container">
              <img src={cont.image_url} alt="container" />
              <div className="details">
                <p>
                  <strong>SIZE:</strong> {cont.size}
                </p>
                <p>
                  <strong>STATUS:</strong>{" "}
                  {cont.status ? "Available" : "Unavailable"}
                </p>
              </div>
              <p id="price">
                <strong>PRICE:</strong>Ksh {cont.price} /=
              </p>
            </div>
          ))}
        </div>
      </>
    );
}

export default Home
