import React, { useState } from "react";
import { Route, Routes, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import Rentnow from "./Rentnow.css";

function RentNow({ spaceDetails }) {
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);
  const [goods, setGoods] = useState("");
  const [pickupStatus, setPickupStatus] = useState("");
  const [deliveryStatus, setDeliveryStatus] = useState("");
  const [errors, setErrors] = useState("");
  const navigate = useNavigate();

  let spaceId = spaceDetails.id;
  console.log(spaceId);

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch("/bookings", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        startDate: startDate,
        endDate: endDate,
        goods: goods,
        pickup_status: pickupStatus,
        delivery_status: deliveryStatus,
        storage_unit_id: spaceId,
      }),
    }).then((res) => {
      if (res.ok) {
        toast.success("Storage unit booked successfully");
        navigate("/");
      } else {
        res.json().then((data) => setErrors(data.error));
        toast.error("An erorr occurred");
      }
    });
  };

  return (
    <div className="booking-container">
      <h1 className="book-space">Book this space</h1>
      <div key={spaceDetails.id}>
        <p>
          <span className="d-names">Unit Name</span>: {spaceDetails.name}
        </p>

        <p>
          <span className="d-names">Unit Size</span>: {spaceDetails.size}
        </p>
        <p>
          <span className="d-names">Unit Price</span>: Ksh.{spaceDetails.price}
        </p>
        <p>
          <span className="d-names">Availability</span>:
          {spaceDetails.status == true ? "Available✅" : "Not Available ❎"}
        </p>
      </div>
      <form className="booking-form" onSubmit={handleSubmit}>
        <p className="error">{errors}</p>
        <label>Period</label>
        <div className="period">
          <label>From</label>
          <DatePicker
            selected={startDate}
            onChange={(date) => setStartDate(date)}
            dateFormat="dd/MM/yyyy"
            minDate={new Date()}
            filterDate={(date) => date.getDay() !== 0}
            isClearable
          />
          <label>To</label>
          <DatePicker
            selected={endDate}
            onChange={(date) => setEndDate(date)}
            dateFormat="dd/MM/yyyy"
            minDate={new Date()}
            filterDate={(date) => date.getDay() !== 0}
            isClearable
          />
        </div>
        <label>Goods</label>
        <input
          type="text"
          value={goods}
          placeholder="Type of goods"
          onChange={(e) => setGoods(e.target.value)}
        />
        <label>Pickup?</label>
        <select
          value={pickupStatus}
          id="pickup"
          onChange={(e) => setPickupStatus(e.target.value)}
        >
          <option value="yes">Yes</option>
          <option value="no">No</option>
        </select>
        <label>Delivery?</label>
        <select
          value={deliveryStatus}
          id="delivery"
          onChange={(e) => setDeliveryStatus(e.target.value)}
        >
          <option value="yes">Yes</option>
          <option value="no">No</option>
        </select>{" "}
        <input value={spaceId} disabled />
        <button type="submit" className="btn btn-primary">
          Submit
        </button>
      </form>
    </div>
  );
}

export default RentNow;
