import React, { useEffect, useState } from 'react'

const Rent = () => {
  const[units, setUnits] = useState([])
  useEffect(() => {
     fetch("/storage_units")
       .then((r) => r.json())
       .then((data) => setUnits(data));
  }, [])

  function handleClick(item) {
    console.log(item.name)
  }

  return (
    <div>
      <h1>All Available units</h1>
      {units
        .filter((unit) => unit.status === true)
        .map((cont) => (
          <div key={cont.id} className="container">
            <img src={cont.image_url} alt="container" />
            <div className="details">
              <p>
                <strong>SIZE:</strong> {cont.size}
              </p>
              <p>
                <strong>STATUS:</strong>{" "}
                {cont.status ? "Available" : "Unavailable"}
              </p>
            </div>
            <p id="price">
              <strong>PRICE:</strong>Ksh {cont.price} /=
            </p>

            <button onClick={() => { handleClick(cont) }}>book now</button>
          </div>
        ))}
    </div>
  );
}

export default Rent
