import React, { useEffect, useState } from "react";

const Available = () => {
  const [units, setUnits] = useState([]);
  useEffect(() => {
    fetch("/storage_units")
      .then((r) => r.json())
        .then((data) => setUnits(data));
  }, []);

  return (
    <div>
      {units.filter((unit) => unit.status === true)
        .map((unit) => (
          <div className="card mb-3" style={{ maxWidth: "540px" }} key={unit.id}>
            <div className="row g-0">
              <div className="col-md-4">
                <img src={unit.image_url} className="img-fluid rounded-start" alt="..." />
              </div>
              <div className="col-md-8">
                <div className="card-body">
                            <h5 className="card-title">Unit Number: {unit.name}</h5>
                  <p className="card-text">
                    This Unit has no items stored in it.
                  </p>
                </div>
              </div>
            </div>

          </div>
        ))}
    </div>
  );
};

export default Available;
