import React, { useState} from "react";
import { useNavigate, Link } from "react-router-dom";
import paperStyle from "../styles/paper";
import errorStyle from "../styles/errorStyle";

import {
  Avatar,
  Grid,
  Paper,
  Typography,
  TextField,
  Button,
  FormControlLabel,
  Checkbox,
  InputAdornment,
  IconButton,
} from "@mui/material";
import VisibilitySharpIcon from "@mui/icons-material/VisibilitySharp";
import VisibilityOffSharpIcon from "@mui/icons-material/VisibilityOffSharp";
import AddCircleOutlineOutlinedIcon from "@mui/icons-material/AddCircleOutlineOutlined";

function SignUpForm() {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirm_password, setConfirmPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [phone_number, setPhoneNumber] = useState(0);
  const [checked, setChecked] = useState(false);
  const [checkboxError, setCheckboxError] = useState("");
  const navigate = useNavigate();
  const [errors, setErrors] = useState([]);

  function handleSubmit(e) {
    e.preventDefault();
    fetch("/clients", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        username: username,
        email: email,
        password: password,
        confirm_password: confirm_password,
        phone_number: phone_number,
      }),
    }).then((res) => {
      if (res.ok) {
        navigate("/login");
        if (checked) {
          navigate("/login");
        } else {
          setCheckboxError("Accept Terms!");
        }
      } else {
        res.json().then((errorData) => setErrors(errorData.errors));
      }
    });
  }

  // Toggling password visibility
  const handlePasswordVisibility = () => {
    showPassword ? setShowPassword(false) : setShowPassword(true);
  };

  const handleCheckbox = (data) => {
    console.log("checked");
  };

  // JSX
  return (
    <Grid>
      <Paper elevation={20} style={paperStyle}>
        <Grid align="center">
          <Avatar style={{ backgroundColor: "#874aff" }}>
            <AddCircleOutlineOutlinedIcon />
          </Avatar>
          <h2 style={{ margin: 0 }}>Sign Up</h2>
          <Typography variant="caption">
            Please fill this form to create an account!
          </Typography>
        </Grid>
        <form onSubmit={handleSubmit}>
          <TextField
            fullWidth
            label="Userame"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Enter your name"
            variant="outlined"
            style={{ margin: "5px" }}
            required
          />
          <TextField
            fullWidth
            label="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Enter your email"
            variant="outlined"
            required
            style={{ margin: "5px" }}
          />
          <TextField
            fullWidth
            label="Phone Number"
            value={phone_number}
            onChange={(e) => setPhoneNumber(e.target.value)}
            placeholder="Enter your phone number"
            variant="outlined"
            style={{ margin: "5px" }}
          />
          <TextField
            fullWidth
            type={showPassword ? "text" : "password"}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            label="Password"
            variant="outlined"
            style={{ margin: "5px" }}
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton
                    onClick={handlePasswordVisibility}
                    aria-label="toggle password"
                    edge="end"
                  >
                    {showPassword ? (
                      <VisibilityOffSharpIcon />
                    ) : (
                      <VisibilitySharpIcon />
                    )}
                  </IconButton>
                </InputAdornment>
              ),
            }}
          />
          <TextField
            fullWidth
            label="Confirm Password"
            value={confirm_password}
            onChange={(e) => setConfirmPassword(e.target.value)}
            variant="outlined"
            style={{ margin: "5px" }}
            type="password"
          />

          <FormControlLabel
            control={
              <Checkbox type="checkbox" onChange={() => setChecked(true)} />
            }
            label="I accept the terms and conditions."
          />
          <Grid>{checkboxError}</Grid>
          <Button type="submit" variant="contained" color="primary">
            Sign Up
          </Button>
        </form>
        <Grid>
          <Typography variant="caption" style={{ fontSize: "15px" }}>
            Already have an account?
          </Typography>
          <Button type="submit" variant="contained" color="primary">
            <Link to="/login">Sign In</Link>
          </Button>
        </Grid>
        <Grid>
          {errors.map((err) => (
            <Grid style={errorStyle} key={err}>
              {err}
            </Grid>
          ))}
        </Grid>
      </Paper>
    </Grid>
  );
}

export default SignUpForm;
